export default {
  data() {
    return {
      userName: '',
      taskDescription: '',
      date: '',
      startTime: '',
      endTime: ''
    };
  },
  methods: {
    submitForm() {
      this.$emit('save-entry', {
        userName: this.userName,
        taskDescription: this.taskDescription,
        date: this.date,
        startTime: this.startTime,
        endTime: this.endTime
      });
      this.clearForm();
    },
    clearForm() {
      this.userName = '';
      this.taskDescription = '';
      this.date = '';
      this.startTime = '';
      this.endTime = '';
    }
  }
};
